//the tick gunction is use to knwo if the player is walking. it is not in the update because the tick function is called at regular interval and it is important.
function tick(entity, model) {
	if(!isrealywalking & (entity.getLimbSwing() - walkingtest)>0.2 &!( entity.isRiding()) &!(entity.getPose() == "swimming")){isrealywalking = true;}
	if(!isslowingdown & entity.getLimbSwing() - walkingtest < 0.2 & isrealywalking){isslowingdown = true;}
	if(((walking_timer < 0.05 | Math.abs(walking_timer - 5) < 0.05 | (10- walking_timer < 0.05)) & isslowingdown) | ( entity.isRiding()) |(entity.getPose() == "swimming"))
	{
		isslowingdown = false;
		isrealywalking = false;
		body_angle = 0;
	}
	if(entity.getLimbSwing() - walkingtest>0.1 & isslowingdown){isslowingdown = false;}
	walkingtest = entity.getLimbSwing();
}